package ca.uvic.seng330.ex7;

public class Driver {

  public static void main(String[] args) {
    Points pObj = new Points();

    //change this so these run on separate threads.
    PointsClient c1 = new CricketPointsClient(pObj);
    PointsClient c2 = new CricketPointsClient(pObj);
    PointsClient c3 = new FootballPointsClient(pObj);

    for (int i = 0; i < 1000; i++) {
      c1.scores(1);
      c2.assists(1);
      c3.scores(1);
      c3.assists(1);
      if(pObj.getPoints() < 10*i)
        c1.assists(-1);
      else c1.assists(-2);
    }
    System.out.println(pObj.getPoints());
  }

}
